and mdr.relation_name in #{item}
and mdr.short_relation_name in #{item}
and cir.to_ci_id = cc.ci_id and cc.class_id = mdc.class_id and cir.relation_id = mdr.relation_id and cir.ci_state_id = cis.ci_state_id and cir.ns_id = ns.ns_id

select cir.ci_relation_id as ciRelationId, cir.ns_id as nsId, ns.ns_path as nsPath, cir.from_ci_id as fromCiId, cir.relation_goid as relationGoid, cir.relation_id as relationId, mdr.relation_name as relationName, cir.to_ci_id toCiId, cir.ci_state_id as relationStateId, cis.state_name as relationState, cir.last_applied_rfc_id as lastAppliedRfcId, cir.comments, cir.created, cir.updated from cm_ci_relations cir, md_relations mdr, cm_ci_state cis, cm_ci cc, md_classes mdc, ns_namespaces ns where cir.from_ci_id = #{fromId} and (#{relationName}::varchar is null or mdr.relation_name = #{relationName}) and (#{shortRelName}::varchar is null or mdr.short_relation_name = #{shortRelName}) and cir.to_ci_id in
#{item}
and cir.to_ci_id = cc.ci_id and cc.class_id = mdc.class_id and cir.relation_id = mdr.relation_id and cir.ci_state_id = cis.ci_state_id and cir.ns_id = ns.ns_id

select cir.ci_relation_id as ciRelationId, cir.ns_id as nsId, ns.ns_path as nsPath, cir.from_ci_id as fromCiId, cir.relation_goid as relationGoid, cir.relation_id as relationId, mdr.relation_name as relationName, cir.to_ci_id toCiId, cir.ci_state_id as relationStateId, cis.state_name as relationState, cir.last_applied_rfc_id as lastAppliedRfcId, cir.comments, cir.created, cir.updated from cm_ci_relations cir, md_relations mdr, cm_ci_state cis, ns_namespaces ns where (#{relationName}::varchar is null or mdr.relation_name = #{relationName}) and (#{shortRelName}::varchar is null or mdr.short_relation_name = #{shortRelName}) and cir.from_ci_id in
#{item}
and cir.relation_id = mdr.relation_id and cir.ci_state_id = cis.ci_state_id and cir.ns_id = ns.ns_id

select cir.ci_relation_id as ciRelationId, cir.ns_id as nsId, ns.ns_path as nsPath, cir.from_ci_id as fromCiId, cir.relation_goid as relationGoid, cir.relation_id as relationId, mdr.relation_name as relationName, cir.to_ci_id toCiId, cir.ci_state_id as relationStateId, cis.state_name as relationState, cir.last_applied_rfc_id as lastAppliedRfcId, cir.comments, cir.created, cir.updated from cm_ci_relations cir, md_relations mdr, cm_ci_state cis, ns_namespaces ns where (#{relationName}::varchar is null or mdr.relation_name = #{relationName}) and (#{shortRelName}::varchar is null or mdr.short_relation_name = #{shortRelName}) and cir.to_ci_id in
#{item}
and cir.relation_id = mdr.relation_id and cir.ci_state_id = cis.ci_state_id and cir.ns_id = ns.ns_id

select

		   cir.from_ci_id
		   cir.to_ci_id
		   ns.ns_path
		   'count'

       as group, count(1) as cnt
from cm_ci_relations cir
     join ns_namespaces ns on cir.ns_id = ns.ns_id

		 join md_relations mdr on cir.relation_id = mdr.relation_id


		 join cm_ci from_ci on cir.from_ci_id = from_ci.ci_id
		 join md_classes from_mdc on from_ci.class_id = from_mdc.class_id


		 join cm_ci to_ci on cir.to_ci_id = to_ci.ci_id
		 join md_classes to_mdc on to_ci.class_id = to_mdc.class_id

where


			 (ns.ns_path like #{nsLike} or ns.ns_path = #{ns})


			 ns.ns_path = #{ns}


			 true




			 and mdr.relation_name = #{relationName}


			 and mdr.short_relation_name = #{shortRelName}



		 and cir.from_ci_id = #{fromCiId}


		 and cir.to_ci_id = #{toCiId}



			 and from_mdc.class_name = #{fromClazzName}


			 and from_mdc.short_class_name = #{fromShortClazzName}




			 and to_mdc.class_name = #{toClazzName}


			 and to_mdc.short_class_name = #{toShortClazzName}






					 from_ci.ci_name ${item.condition} #{item.avalue}


					 from_ci.ci_id ${item.condition} #{Litem.avalueAsLong}


					 to_ci.ci_name ${item.condition} #{item.avalue}


					 to_ci.ci_id ${item.condition} #{item.avalueAsLong}


					 exists (select 1 from cm_ci_relation_attributes cra, md_relation_attributes mra
					 where cra.ci_relation_id = cir.ci_relation_id
					 and cra.attribute_id = mra.attribute_id
					 and mra.attribute_name = #{item.attributeName}
					 and cra.dj_attribute_value ${item.condition} #{item.avalue})





	 group by 1

select cir.ci_relation_id as ciRelationId, cir.ns_id as nsId, ns.ns_path as nsPath, cir.from_ci_id as fromCiId, cir.relation_goid as relationGoid, cir.relation_id as relationId, mdr.relation_name as relationName, cir.to_ci_id toCiId, cir.ci_state_id as relationStateId, cis.state_name as relationState, cir.last_applied_rfc_id as lastAppliedRfcId, cir.comments, cir.created, cir.updated from cm_ci_relations cir join md_relations mdr on cir.relation_id = mdr.relation_id join cm_ci_state cis on cir.ci_state_id = cis.ci_state_id join ns_namespaces ns on cir.ns_id = ns.ns_id
join cm_ci from_ci on cir.from_ci_id = from_ci.ci_id
join ns_namespaces target_ns on from_ci.ns_id = target_ns.ns_id
join md_classes from_mdc on from_ci.class_id = from_mdc.class_id
where cir.to_ci_id = #{toId}
and mdr.relation_name = #{relationName}
and mdr.short_relation_name = #{shortRelName}
and from_mdc.class_name = #{fromClazzName}
and from_mdc.short_class_name = #{fromShortClazzName}
and (target_ns.ns_path like #{fromNsPathLike} or target_ns.ns_path = #{fromNsPath})
and target_ns.ns_path = #{fromNsPath}

select cir.ci_relation_id as ciRelationId, cir.ns_id as nsId, ns.ns_path as nsPath, cir.from_ci_id as fromCiId, cir.relation_goid as relationGoid, cir.relation_id as relationId, mdr.relation_name as relationName, cir.to_ci_id toCiId, cir.ci_state_id as relationStateId, cis.state_name as relationState, cir.last_applied_rfc_id as lastAppliedRfcId, cir.comments, cir.created, cir.updated from cm_ci_relations cir, md_relations mdr, cm_ci_state cis, cm_ci cc, md_classes mdc, ns_namespaces ns where cir.to_ci_id = #{toId} and (#{relationName}::varchar is null or mdr.relation_name = #{relationName}) and (#{shortRelName}::varchar is null or mdr.short_relation_name = #{shortRelName}) and cir.from_ci_id in
#{item}
and cir.from_ci_id = cc.ci_id and cc.class_id = mdc.class_id and cir.relation_id = mdr.relation_id and cir.ci_state_id = cis.ci_state_id and cir.ns_id = ns.ns_id

select cir.ci_relation_id as ciRelationId, cir.ns_id as nsId, ns.ns_path as nsPath, cir.from_ci_id as fromCiId, cir.relation_goid as relationGoid, cir.relation_id as relationId, mdr.relation_name as relationName, cir.to_ci_id toCiId, cir.ci_state_id as relationStateId, cis.state_name as relationState, cir.last_applied_rfc_id as lastAppliedRfcId, cir.comments, cir.created, cir.updated from cm_ci_relations cir join ns_namespaces ns on cir.ns_id = ns.ns_id join md_relations mdr on cir.relation_id = mdr.relation_id join cm_ci_state cis on cir.ci_state_id = cis.ci_state_id
join cm_ci from_ci on cir.from_ci_id = from_ci.ci_id join md_classes from_mdc on from_ci.class_id = from_mdc.class_id
join cm_ci to_ci on cir.to_ci_id = to_ci.ci_id join md_classes to_mdc on to_ci.class_id = to_mdc.class_id
where
(ns.ns_path like #{nsLike} or ns.ns_path = #{ns})
ns.ns_path = #{ns}
true
and mdr.relation_name = #{relationName}
and mdr.short_relation_name = #{shortRelName}
and from_mdc.class_name = #{fromClazzName}
and from_mdc.short_class_name = #{fromShortClazzName}
and to_mdc.class_name = #{toClazzName}
and to_mdc.short_class_name = #{toShortClazzName}
from_ci.ci_name ${item.condition} #{item.avalue}
from_ci.ci_id ${item.condition} #{Litem.avalueAsLong}
to_ci.ci_name ${item.condition} #{item.avalue}
to_ci.ci_id ${item.condition} #{item.avalueAsLong}
exists (select 1 from cm_ci_relation_attributes cra, md_relation_attributes mra where cra.ci_relation_id = cir.ci_relation_id and cra.attribute_id = mra.attribute_id and mra.attribute_name = #{item.attributeName} and cra.dj_attribute_value ${item.condition} #{item.avalue})

select cir.ci_relation_id as ciRelationId, cir.ns_id as nsId, ns.ns_path as nsPath, cir.from_ci_id as fromCiId, cir.relation_goid as relationGoid, cir.relation_id as relationId, mdr.relation_name as relationName, cir.to_ci_id toCiId, cir.ci_state_id as relationStateId, cis.state_name as relationState, cir.last_applied_rfc_id as lastAppliedRfcId, cir.comments, cir.created, cir.updated from cm_ci_relations cir, md_relations mdr, cm_ci_state cis, cm_ci cc, md_classes mdc, ns_namespaces ns where cir.from_ci_id = #{fromId}
and mdr.relation_name = #{relationName}
and mdr.short_relation_name = #{shortRelName}
and mdc.class_name = #{toClazzName}
and cir.to_ci_id = cc.ci_id and cc.class_id = mdc.class_id and cir.relation_id = mdr.relation_id and cir.ci_state_id = cis.ci_state_id and cir.ns_id = ns.ns_id
cc.ci_name ${item.condition} #{item.avalue}
exists (select 1 from cm_ci_relation_attributes cra, md_relation_attributes mra where cra.ci_relation_id = cir.ci_relation_id and cra.attribute_id = mra.attribute_id and mra.attribute_name = #{item.attributeName} and cra.dj_attribute_value ${item.condition} #{item.avalue})

select cir.ci_relation_id as ciRelationId, cir.ns_id as nsId, ns.ns_path as nsPath, cir.from_ci_id as fromCiId, cir.relation_goid as relationGoid, cir.relation_id as relationId, mdr.relation_name as relationName, cir.to_ci_id toCiId, cir.ci_state_id as relationStateId, cis.state_name as relationState, cir.last_applied_rfc_id as lastAppliedRfcId, cir.comments, cir.created, cir.updated from cm_ci_relations cir, md_relations mdr, cm_ci_state cis, cm_ci cc, md_classes mdc, ns_namespaces ns where cir.to_ci_id = #{toId}
and mdr.relation_name = #{relationName}
and mdr.short_relation_name = #{shortRelName}
and mdc.class_name = #{fromClazzName}
and cir.from_ci_id = cc.ci_id and cc.class_id = mdc.class_id and cir.relation_id = mdr.relation_id and cir.ci_state_id = cis.ci_state_id and cir.ns_id = ns.ns_id
cc.ci_name ${item.condition} #{item.avalue}
exists (select 1 from cm_ci_relation_attributes cra, md_relation_attributes mra where cra.ci_relation_id = cir.ci_relation_id and cra.attribute_id = mra.attribute_id and mra.attribute_name = #{item.attributeName} and cra.dj_attribute_value ${item.condition} #{item.avalue})


with platforms as (
	select ci.ci_id
	from cm_ci ci, md_classes md
	where ci.class_id = md.class_id
	  and md.class_name = #{platformClass}
	  and exists(
	    select 1 from (
	    	select count(1) as count
	  		from cm_ci_attributes attr, md_class_attributes mdattr
	  		where attr.ci_id = ci.ci_id
	  	  	  and attr.attribute_id = mdattr.attribute_id
	  	  	  and (

		 		  mdattr.attribute_name = #{item.attributeName} and attr.df_attribute_value ${item.condition} #{item.avalue}

	  	  	  )
	    ) as t1
	    where count = #{attrListSize}
	  )
)
select count(1)
from platforms pl, cm_ci_relations rel, md_relations mdrel, cm_ci_relation_attributes attr, md_relation_attributes mdattr
where pl.ci_id = rel.from_ci_id
  and rel.relation_id = mdrel.relation_id
  and mdrel.relation_name = #{requiresRelation}
  and rel.ci_relation_id = attr.ci_relation_id
  and attr.attribute_id = mdattr.attribute_id
  and mdattr.attribute_name = 'template'
  and attr.df_attribute_value = #{tmplCiName}


with platforms as (
	select ci.ci_id
	from cm_ci ci, md_classes md
	where ci.class_id = md.class_id
	  and md.class_name = #{platformClass}
	  and exists(
	    select 1 from (
	    	select count(1) as count
	  		from cm_ci_attributes attr, md_class_attributes mdattr
	  		where attr.ci_id = ci.ci_id
	  	  	  and attr.attribute_id = mdattr.attribute_id
	  	  	  and (

		 		  mdattr.attribute_name = #{item.attributeName} and attr.df_attribute_value ${item.condition} #{item.avalue}

	  	  	  )
	    ) as t1
	    where count = #{attrListSize}
	  )
)
select count(*) 
from (
	select rel.to_ci_id ci_id, pl.ci_id pl_id
	from platforms pl, cm_ci_relations rel, md_relations mdrel, cm_ci_relation_attributes attr, md_relation_attributes mdattr
	where pl.ci_id = rel.from_ci_id
	  and rel.relation_id = mdrel.relation_id
	  and mdrel.relation_name = #{requiresRelation}
	  and rel.ci_relation_id = attr.ci_relation_id
	  and attr.attribute_id = mdattr.attribute_id
	  and mdattr.attribute_name = 'template'
	  and attr.df_attribute_value = #{fromTmplCiName}
) as from_cis,
   (select rel.to_ci_id ci_id, pl.ci_id pl_id
	from platforms pl, cm_ci_relations rel, md_relations mdrel, cm_ci_relation_attributes attr, md_relation_attributes mdattr
	where pl.ci_id = rel.from_ci_id
	  and rel.relation_id = mdrel.relation_id
	  and mdrel.relation_name = #{requiresRelation}
	  and rel.ci_relation_id = attr.ci_relation_id
	  and attr.attribute_id = mdattr.attribute_id
	  and mdattr.attribute_name = 'template'
	  and attr.df_attribute_value = #{toTmplCiName}
) as to_cis, cm_ci_relations rel
where from_cis.pl_id = to_cis.pl_id 
   and rel.from_ci_id = from_cis.ci_id
   and rel.to_ci_id = to_cis.ci_id
