SELECT rci.rfc_id as rfcId, rci.release_id as releaseId, rci.ci_id as ciId, rci.ns_id as nsId, ns.ns_path as nsPath, rci.class_id as ciClassId, mdc.class_name as ciClassName, mdc.impl as impl, rci.ci_name as ciName, rci.ci_goid as ciGoid, rci.action_id as rfcActionId, a.action_name as rfcAction, rci.execution_order as execOrder, rci.is_active_in_release as isActiveInRelease, rci.last_rfc_id lastAppliedRfcId, r.release_type as releaseType, rci.comments, rci.created_by as createdBy, rci.updated_by as updatedBy, rci.created_by as rfcCreatedBy, rci.updated_by as rfcUpdatedBy, rci.created as rfcCreated, rci.updated as rfcUpdated, rci.created, rci.updated FROM dj_rfc_ci rci join ns_namespaces ns on rci.ns_id = ns.ns_id join md_classes mdc on rci.class_id = mdc.class_id join dj_releases r on rci.release_id = r.release_id join dj_rfc_ci_actions a on rci.action_id = a.action_id join dj_release_states rs on r.release_state_id = rs.release_state_id where ns.ns_path like #{nsPath}
and mdc.class_name = #{ciClassName}
and mdc.short_class_name = #{ciClassName}
and rci.created >= #{startDate}
and rci.created <= #{endDate}
and rci.is_active_in_release = 'true' and rs.state_name ='closed'

