SELECT rci.rfc_id as rfcId, rci.release_id as releaseId, rci.ci_id as ciId, rci.ns_id as nsId, ns.ns_path as nsPath, rci.class_id as ciClassId, mdc.class_name as ciClassName, mdc.impl as impl, rci.ci_name as ciName, rci.ci_goid as ciGoid, rci.action_id as rfcActionId, a.action_name as rfcAction, rci.execution_order as execOrder, rci.is_active_in_release as isActiveInRelease, rci.last_rfc_id lastAppliedRfcId, r.release_type as releaseType, rci.comments, rci.hint, rci.created_by as createdBy, rci.updated_by as updatedBy, rci.created_by as rfcCreatedBy, rci.updated_by as rfcUpdatedBy, rci.created as rfcCreated, rci.updated as rfcUpdated, rci.created, rci.updated FROM dj_rfc_ci rci join ns_namespaces ns on rci.ns_id = ns.ns_id join md_classes mdc on rci.class_id = mdc.class_id join dj_releases r on rci.release_id = r.release_id join dj_rfc_ci_actions a on rci.action_id = a.action_id join dj_release_states rs on r.release_state_id = rs.release_state_id WHERE rci.ci_id = #{ciId} and rci.rfc_id > #{afterRfcId}
and rci.rfc_id <= #{toRfcId}
and rci.is_active_in_release = 'true' and rs.state_name ='closed'

select r.release_id as releaseId, r.ns_id as nsId, r.parent_release_id as parentReleaseId, ns.ns_path as nsPath, r.release_name as releaseName, r.created_by as createdBy, r.commited_by as commitedBy, r.release_state_id as releaseStateId, rs.state_name as releaseState, r.release_type as releaseType, r.description, r.revision, r.created, r.updated from dj_releases r, ns_namespaces ns, dj_release_states rs where r.ns_id = ns.ns_id and ns.ns_path = #{releaseNs} and r.release_state_id = rs.release_state_id
and r.release_id ${order.aboveOperator} #{releaseOffset}
and r.release_id ${order.belowOperator} #{endRelId}
order by r.release_id ${order.sort}
limit #{limit}

select r.release_id as releaseId, r.ns_id as nsId, r.parent_release_id as parentReleaseId, ns.ns_path as nsPath, r.release_name as releaseName, r.created_by as createdBy, r.commited_by as commitedBy, r.release_state_id as releaseStateId, rs.state_name as releaseState, r.release_type as releaseType, r.description, r.revision, r.created, r.updated from dj_releases r, ns_namespaces ns, dj_release_states rs where r.release_id in ( select distinct r.release_id from dj_releases r, dj_rfc_ci rfc where r.release_id = rfc.release_id and r.ns_id = (select ns_id from ns_namespaces where ns_path = #{releaseNs}) and (rfc.ns_id in (select ns_id from ns_namespaces where ns_path like #{rfcNsFilter}) or rfc.ci_name like #{wildcardFilter})
and rfc.release_id not in #{item}
and r.release_id ${order.aboveOperator} #{releaseOffset}
and r.release_id ${order.belowOperator} #{endRelId}
order by r.release_id ${order.sort}
limit #{limit}
) and r.ns_id = ns.ns_id and r.release_state_id = rs.release_state_id order by r.release_id ${order.sort}

select r.release_id as releaseId, r.ns_id as nsId, r.parent_release_id as parentReleaseId, ns.ns_path as nsPath, r.release_name as releaseName, r.created_by as createdBy, r.commited_by as commitedBy, r.release_state_id as releaseStateId, rs.state_name as releaseState, r.release_type as releaseType, r.description, r.revision, r.created, r.updated from dj_releases r, ns_namespaces ns, dj_release_states rs where r.release_id in ( select distinct r.release_id from dj_releases r, dj_rfc_relation rfc where r.release_id = rfc.release_id and r.ns_id = (select ns_id from ns_namespaces where ns_path = #{releaseNs}) and (rfc.ns_id in (select ns_id from ns_namespaces where ns_path like #{rfcNsFilter}))
and rfc.release_id not in #{item}
and r.release_id ${order.aboveOperator} #{releaseOffset}
and r.release_id ${order.belowOperator}= #{endRelId}
order by r.release_id ${order.sort}
limit #{limit}
) and r.ns_id = ns.ns_id and r.release_state_id = rs.release_state_id order by r.release_id ${order.sort}

