    LANGUAGE plpgsql
    AS $$
DECLARE
l_old_state integer;
l_desc text;
l_comments text;
l_ops text;
l_updated_by character varying;
BEGIN

    select state_id into l_old_state
    from dj_deployment
    where deployment_id = p_deployment_id;

