CREATE FUNCTION dj_rm_rfc_ci(p_rfc_id bigint) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
	l_ci_id bigint;
	l_ci_exists integer;
	l_rel_rfc_id bigint;
