#{item}
and rci.release_id = r.release_id and r.release_state_id = rs.release_state_id and rs.state_name = 'open' and rci.is_active_in_release = true and rci.ns_id = ns.ns_id and rci.class_id = cl.class_id and rci.action_id = a.action_id

